Grundfos.GlobalMenu = (function($) {
	"use strict";
	var slideOpen = true;
	var toggling = false;

	// private methods
	//==============================================================================================
	var slidein = function() {
		clearTimeout(toggling);
		if (!slideOpen) {
			$("#megadropdown").slideDown("normal");
		} else {
			$("#megadropdown").show();
		}
		slideOpen = true;
		$(this).addClass("active");
		return false;
	};

	var slideout = function() {
		clearTimeout(toggling);
		if (!slideOpen) {
			$("#megadropdown").hide();
		} else {
			$("#megadropdown").slideUp("normal");
		}
		slideOpen = false;
		$(this).removeClass("active");
		return false;
	};
	
	var unhover = function() {
		clearTimeout(toggling);
		toggling = setTimeout(slideout, 300);
	};
	
	var hoverme = function() {
		clearTimeout(toggling);
		toggling = setTimeout(slidein, 300);
	};

	var focusSubMenu = function(index) {
		$("#megadropdown").find("> div").hide();
		$("#megadropdown").fadeIn("fast");
		$("#submenu" + index).fadeIn("fast");
	};

	var getSelectedElement = function(item) {
		return $("#navmain").find("li").index(item) + 1;
	};

	// public methods
	//==============================================================================================
	return {
		initialize: function(navigationStyle) {
			if(navigationStyle === "nav-normal") {
				$("#navmain").find("a").hover(hoverme, unhover);
				$("#megadropdown").hover(hoverme, unhover);

				$("#navmain").find("li").hover( function(event) {
					event.preventDefault();
					event.stopPropagation();

					var index = getSelectedElement($(this));
					focusSubMenu(index);
				});
			} else if(navigationStyle === "nav-alternate") {
				var selectedIndex = -1;

				$("#navmain").find("li").click( function(event) {
					event.preventDefault();
					event.stopPropagation();

					var index = getSelectedElement($(this));
					if(index === selectedIndex) {
						selectedIndex = -1;
						$("#megadropdown").fadeOut("fast");
					} else {
						selectedIndex = index;
						focusSubMenu(index);
					}
				});

				$('html').click(function() {
					$("#megadropdown").fadeOut("fast");
				});
			} else {
				console.log("Grundfos.GlobalMenu.initialize - unknown navigationStyle");
			}
		}
	};
}(jQuery));

