var TabbedPanel = TabbedPanel || { };

TabbedPanel.tabsWithHistory = (
        function(tabSelector, panesSelector) {

          if ($(tabSelector).length == 0)
            return;
        
          $(tabSelector).tabs(panesSelector);
          var api = $(tabSelector).data("tabs");
          var tabs = api.getTabs();
    
          var subTabs = $("ul.nested.tabs a");
    
          $.tools.history.init(tabs.add(subTabs));

          var idToIndex = {}
          tabs.each(function(index, tab){
            idToIndex[tab.id]=index;
          });
        
          function selectTab() {
              var ids = location.hash.split('-');
              if (ids.length > 0) { 
                  var tabid = '_'+ids[0].slice(1);
                  if (typeof(idToIndex[tabid]) != 'undefined') {
                   api.click(idToIndex[tabid]);
                   if (ids.length == 1) { // Handle selection of nested panels. History event is not fired on thoose in this case
                     api.getCurrentPane()
                        .find('.tabs:data(tabs)')
                        .each(function(index, nestedPanel){
                            var nestedApi = $(nestedPanel).data('tabs');
                            nestedApi.click(0);
                        }); 
                   }
                  }
              }
          }
          
          // Setup history click functionality
          tabs.each(function(i) {                 
              $(this).bind('history', function(e) {
                   if (location.hash) {
                     selectTab();
                   }
                   else {
                        api.click(0);
                   }               
                  return e.preventDefault();
              });         
          });
        
           // open initial tab main tab
           if (location.hash) {
                selectTab();
           }    
       }
   )     
        
       
TabbedPanel.nestedTabsWithHistory = (
     function(tabSelector, panesSelector) {
      if ($(tabSelector).length == 0)
        return;
      $(tabSelector).tabs(panesSelector);
      var api = $(tabSelector).data("tabs");
      var subTabs = api.getTabs();
      
      // Update the anchors according to the outer panel
      subTabs.each(function () {
        if ($(this).attr('href').indexOf('-') < 0) {
            $(this).attr('href', '#'+$(this).closest('.pane.main').attr('tabanchor')+'-'+$(this).attr('href').slice(1));
        }
      });
      
      // Note: History is initialized in the main tab
      var idToIndex = {}
      subTabs.each(function(index, tab){
        idToIndex[tab.id]=index;
      });
      
      function selectTab() {
          var ids = location.hash.split('-');
           if (ids.length > 1) { // Handle selection of outer panels. History event is not fired on thoose in this case
             api.getCurrentPane()
                .closest('.main.panes')
                .siblings('.tabs.main:data(tabs)')
                .each(function(index, outerPanel){
                    var outerApi = $(outerPanel).data('tabs');
                    outerApi.getTabs().each(function(idx, tab) {
                        if (tab.id == '_'+ids[0].slice(1)) {
                            outerApi.click(idx);
                        }
                    })
                });
              var tabid = '_'+ids[1];
              if (typeof(idToIndex[tabid]) != 'undefined') {
               api.click(idToIndex[tabid]);
              }
           }
      }
      
      // Setup history click functionality
      subTabs.each(function(i) {                 
          $(this).bind('history', function(e) {
               if (location.hash) {
                   selectTab();
               } else {
                api.click(0);
               }              
              return e.preventDefault();
          });         
      });
    
       //open initial sub tab 
       if (location.hash) {
          selectTab();
       }
    
    }
  );


