function switchPromo(newPromo, isactive){
	isactive = isactive == null ? true : isactive;
	
	if(isArray(newPromo)){
		if(isactive){
			var myNewPromo = newPromo[1];
			clearTimeout(storyTimeout);
		}else{
			var myNewPromo = newPromo[0];
			setStoryTimeout(switchToNextStory, 10000)
		}
	}else{
		var myNewPromo = newPromo;
		setStoryTimeout(switchToNextStory, 10000)
	}
	//var myNewPromo = isArray(newPromo) ? newPromo[0] : newPromo;
	
	$("#top").fadeOut("fast", function(){
		$(this).html(myNewPromo).fadeIn("fast");
	});
}

