var Grundfos = Grundfos || { };

Grundfos.CountrySelector = (function() {
	var countryMapIsInitialized = false;

	// private methods
	//==============================================================================================
	var strFormat = function(format) {
	    var formatted = format;
	    for (var i = 1; i < arguments.length; i++) {
	        var regexp = new RegExp('\\{' + (i-1) + '\\}', 'gi');
	        formatted = formatted.replace(regexp, arguments[i]);
	    }
	    return formatted;
	};

	var handleRegion = function (regionName, countries, cStart, cEnd) {
		jQuery.each(countries.slice(cStart, cEnd), function(index, item) {
	        var cName = item.name;
	        var cUrl = item.url;

	        var elem = jQuery( strFormat("\#region_{0}", regionName) );
	        elem.append( strFormat("<li><a href='{0}'>{1}</a></li>",cUrl, cName) );
		});
	};

	var setupCountries = function (regionToCountriesMap) {
		jQuery.each(regionToCountriesMap.regions, function(index, item) {
			var regionName = item.name;
			var countries = item.countries;
			handleRegion(regionName, countries, 0, countries.length);
		});
		
		// remap countries from array-of-objects to array-of-arrays for the auto-complete functionality.
		jQuery.each(regionToCountriesMap.countries, function(index, item) {
			regionToCountriesMap.countries[index] = [item.name, item.url];
		});
		
		var countrySelector = new autocomplete("countrySearch", "countrySelect", regionToCountriesMap.countries, this.gotoCountry);
		jQuery(".countryholderen a").click(function() { funnelTracking() });
		countryMapIsInitialized = true;
	};

	var initializeCountryMap = function () {
		if(countryMapIsInitialized) {
			return;
		}
		
		jQuery.ajax({
			url: '/countries/jcr:content.countryselector.json',
			dataType: 'json',
			cache: true,
			success: function(data) {
				setupCountries(data);
			}
		});
	};

	// public methods
	//==============================================================================================
	return {
		gotoCountry: function(site) {
			if( (0 !== site.indexOf("http")) && (0 !== site.indexOf("//"))) {
				site = "//" + site;
			}
			window.location.href = site;
		},

		initialize: function () {
			jQuery("a[rel=#countryselector_wrap]").overlay({
				expose: 'black',
				onBeforeLoad: function() {
					initializeCountryMap();
				}
			});
		}
	}
} ( /* Grundfos.CountrySelector */ ));

/*
 * Slide configuration helper for the front page rotator/slider.
 * 
 * The reason for the "isInitialized" is that the "jQuery advanced slider" component we use cause
 * javascript blocks for each slide to be re-executed as part of it's initialization.
 */
var Grundfos = Grundfos || { };

Grundfos.FrontPageSlider = (function() {
	var isInitialized = false;
	var slideProperties = [];

	// public methods
	//==============================================================================================
	return {
		addSlideConfig: function(slideConf) {
			if(!isInitialized) {
				slideProperties.push(slideConf);
			} else {
				// console.log("Trying to push after init");
			}
		},
		
		getSlideProperties: function() {
			return slideProperties.slice(0);	// defensive copy of the array - not a deep copy of the objects.
		},
		
		doneInitializing: function() {
			isInitialized = true;
		}
	}
} ());



function autocomplete(input, select, elements, onclick){
	var searchField = document.getElementById(input);
	var suggestSelect = document.getElementById(select);
	
	searchField.onkeydown = function(evt){
		evt = evt ? evt : event;
		
	  	switch (evt.keyCode){
			case 33:	moveCursor(-20);		break;	// PgUp
			case 34:	moveCursor(20);			break;	// PgDn
			case 35:	moveCursor(100000); 	break;	// End
			case 36:	moveCursor(-100000); 	break;	// Home
			case 38:	moveCursor(-1); 		break;	// Up
			case 40:	moveCursor(1); 			break;	// Down
			default:							return;
	  	}
	}
	
	searchField.onkeyup = function(evt){
		evt = evt ? evt : event;

		switch (evt.keyCode){
	  		//case  8:	break;	// Back space
			case  9:	break;	// Tab
			case 13:	selectSuggestion(); break;	// Enter
			case 16:	break;	// Shift
			case 17:	break;	// Ctrl
			case 18:	break;	// Alt
			case 19:	break;	// Pause
			case 20:	break;	// Caps Lock
			//case 32:	break;	// Space
			case 27:	escape(); break; // Esc
			case 33:	break;	// PgUp
			case 34:	break;	// PgDn
			case 35:	break;	// End
			case 36:	break;	// Home
			case 37:	break;	// Left
			case 38:	break;	// Up
			//case 39:	break;	// Right
			case 39:	selectSuggestion(); 	break;	// Right
			case 40:	break;	// Dowm
			case 44:	break;	// Print Scr
			case 45:	break;	// Insert
			//case 46:	break;	// Delete
			case 112:	break;	// F1
			case 113:	break;	// F2
			case 123:	break;	// F12
			case 144:	break;  // Num Lock
			case 145:	break;  // Scroll Lock                
			default:	populateOptions();	break;
	  	}
	}
	
	searchField.onkeypress = function(evt){
		evt = evt ? evt : event;
		
		if(isNaN(evt.charCode)) // IE
			return evt.keyCode != 13; // Enter
		else // Firefox etc.
			return evt.keyCode != 13 // Enter
				&& evt.keyCode != 35 // End or #
				&& evt.keyCode != 36 // Home or $
				&& evt.keyCode != 38 // <arrow up> or "&"
				&& evt.keyCode != 40;// <arrow down>  or "(" 	
	}
	
	var cursorIndex = 0;

	function moveCursor(delta){
		cursorIndex = cursorIndex + delta;
		
		if(cursorIndex < 0 ) cursorIndex = 0;
		
		if(cursorIndex >= suggestSelect.options.length) cursorIndex = suggestSelect.options.length - 1;
			
		suggestSelect.selectedIndex = cursorIndex;	
	}

	/* Populate suggestSelect dropdown options */
	function populateOptions(){
		cursorIndex = 0;
		
		suggestSelect.innerHTML="";
		
		if(searchField.value.length == 0) suggestSelect.innerHTML = "";
		else{
			try{	
				var searchStr = searchField.value.replace(/[ ]$/,'').replace(/[+]/g,'plus').replace(/[,\(\)]/g,'');
				var re = new RegExp("\\b" + searchStr,'i')
				var reStripped = new RegExp("\\b" + searchStr.replace(/[ ]/g,''),'i')

				for(var i = 0; i < elements.length; i++){
					var name = elements[i][0].replace(/[+]/g,'plus').replace(/[,\(\)]/g,'');
					
					if(name.match(re) || name.replace(/[ ]/g,'').match(reStripped)) {
						var option = document.createElement("option");
						option.setAttribute("value", elements[i][1]);
						option.innerHTML = elements[i][0];
						suggestSelect.appendChild(option);
					}
				}
			}catch(e){ alert(e); } // RegExp exceptions eg. "("
		}
		
		if(suggestSelect.options.length > 0 && searchField.value.length > 0) {
			suggestSelect.style.visibility = "visible";
			suggestSelect.size = (suggestSelect.options.length > 20 ? 20 : suggestSelect.options.length);
			
			if(suggestSelect.options.length == 1) suggestSelect.selectedIndex = 0;
		}
		else{
			suggestSelect.style.visibility = "hidden";
			suggestSelect.size = 0;
		}
	}

	/* Find match in dropdown list */
	function selectSuggestion(){
		onclick(suggestSelect.value);
	}

	function escape(){
		searchField.value = "";
		suggestSelect.style.visibility = "hidden";
		suggestSelect.size = 0;
	}
}


