/*global Grundfos:true */	// keep jshint/jslint happy
Grundfos = Grundfos || { };
Grundfos.Forms = Grundfos.Forms || { };

Grundfos.Forms.Mapper = ( function() {
	"use strict";

	var _debug = false;
	var _forms = {};		// set of forms we have mappings for 
	
	// private methods
	//==============================================================================================
	var debugLog = function(message) {
		if(_debug && 
			(typeof console !== undefined) &&
			(typeof console.log === "function")
		) {
			console.log(message);
		}
	};
	
	var lookupField = function(formId, field) {
		var result;
		
		if(field[0] === '#') {
			// lookup by absolute id
			result = jQuery(field);
		} else {
			// lookup relative to enclosing form
			result = jQuery("#" + formId).find("[name=" + field + "]");
		}
		
		return result;
	};

	var applyOneMapping = function(form, mapping) {
		var sourceField = lookupField(form.id, mapping.source);
		var targetField = lookupField(form.id, mapping.target);
		var sourceVal = sourceField.val().toLowerCase();
		
		if( (sourceField.length === 0) || (targetField.length === 0) ) {
			debugLog("source or target field doesn't exist"); 
			return;
		}
		
		debugLog("Applying mapping from " + mapping.source + " => " + mapping.target);
		for(var key in mapping.mappings) {
			if(mapping.mappings.hasOwnProperty(key)) {
				if(sourceVal === key.toLowerCase() ) {
					debugLog("\tFound match: " + sourceVal + " / " + key);
					targetField.val( mapping.mappings[key] );
				}
			}
		}
	};
	
	// applies all mappings for one form
	var applyAllMappings = function (form) {
		for(var index=0, length=form.mappings.length; index<length; ++index) {
			var mapping = form.mappings[index];
			applyOneMapping(form, mapping);
		}
	};
	
	// adds on-change hook to a single form element
	var addElementHook = function(form, mapping) {
		var sourceField = lookupField(form.id, mapping.source);
		
		if(sourceField.is("select")) {
			sourceField.change( function() { applyAllMappings(form); } );
		} else {
			sourceField.blur( function() { applyAllMappings(form); } );
		}
	};
	
	// public methods
	//==============================================================================================
	return {
		addMapping: function(formId, mapping) {
			if( typeof mapping.source === "undefined" ||
				typeof mapping.target === "undefined" ||
				typeof mapping.mappings === "undefined"
			) {
				debugLog("Warning: tried to add malformed mapping");
				return;
			}
				
			if(typeof _forms[formId] === "undefined") {
				// initialize new mapping - note that the mappings field is a one-element array. 
				debugLog("Initializing form " + formId);
				_forms[formId] = { id: formId, mappings: [mapping] };
			} else {
				// add to existing mapping list
				debugLog("Adding to form" + formId);
				_forms[formId].mappings.push(mapping);
			}
		},
		
		// hookupForm adds on-change events to all form elements with mappings defined.
		// Call when you've applied all necessary addMapping calls.
		hookupForm: function(formId) {
			var form = _forms[formId]; 
			if(typeof form !== "undefined") {
				debugLog("Processing form [" + formId + "]");
				for(var index=0, length=form.mappings.length; index < length; ++index) {
					var mapping = form.mappings[index];
					debugLog("\tmapping from " + mapping.source + " to " + mapping.target);
					addElementHook(form, mapping);
				}
			} else {
				debugLog("form undefined: " + formId);
			}
		},

		// applies all mappings for a given form 
		applyMappings: function(formId) {
			var form = _forms[formId];
			if(typeof form !== "undefined") {
				applyAllMappings(form);
			}
		}
	};
}() );

